import React, { useEffect, useState } from "react";
import RootApp from "./RootApp.js";
import "./styles/RootApp.css";
import qrCodeIcon from "./images/qr-code.png";
function App() {
  const tg = window.Telegram.WebApp;
  const [loading, setLoading] = useState(true);
  const loader = document.getElementById("spinner");
  let isTelegram = true;
  if (Object.keys(tg.initDataUnsafe).length == 0) {
    isTelegram = false;
  }
  if (loader){
      setTimeout(() => {
      loader.style.display = "none";
      setLoading(false);
    }, 3000);
  }
  useEffect(() => {
    tg.ready();
    tg.expand();
  }, []);
  if (isTelegram){
    return (
      !loading && (
      <div className="App">
        <RootApp />
      </div>
      )
    );
  }
  else{
  return(
    !loading && (
      <div className="App">
        <h6>Please open Telegram</h6>
        <img src={qrCodeIcon} className="qrCodeIcon" /> 
      </div>
      )
  );
}
}
export default App;
