import React, { useEffect, useState } from "react";
import RootApp from "./RootApp.js";
import "./styles/RootApp.css";

function App() {
  const tg = window.Telegram.WebApp;
  const isTelegram = tg.initDataUnsafe.user.username;
  useEffect(() => {
    tg.ready();
    tg.expand();
  }, []);
  const [loading, setLoading] = useState(true);
  const loader = document.getElementById("spinner");
  if (loader){
    setTimeout(() => {
      loader.style.display = "none";
      setLoading(false);
    }, 2000);
  }

  if (isTelegram){
  return (
    !loading && (
    <div className="App">
      <RootApp />
    </div>
    )
  );
  }else{
    return (
      <div className="App">
        Please auth
      </div>
    );
  }
}
export default App;
