import React, { useState } from "react";
import RootApp from "./RootApp.js";
import "./styles/RootApp.css";

function App() {
  const [loading, setLoading] = useState(true);
  const loader = document.getElementById("spinner");
  if (loader){
    setTimeout(() => {
      loader.style.display = "none";
      setLoading(false);
    }, 2000);
  }

  return (
    !loading && (
    <div className="App">
      <RootApp />
    </div>
    )
  );
}
export default App;
