import React, {useState, useEffect} from "react";

import { Routes, Route, Link} from "react-router-dom";

import { MainPage } from "./pages/MainPage.js";
import { StorePage } from "./pages/StorePage.js";
import { QuestsPage } from "./pages/QuestsPage.js";
import { ReferalsPage } from "./pages/ReferalsPage.js";
import { ErrorPage } from "./pages/ErrorPage.js";
import { ProfilePage } from "./pages/ProfilePage.js";

import Navigation from "./Navigation.js";

export default function RootApp(){

    const [money, setMoney] = useState(0);
    const [energy, setEnergy] = useState(100);

    const handleClick = () => {
        if (energy <= 0){
            return;
        }
        setMoney(money => money + 1);
        setEnergy(energy => energy - 1);
        document.getElementById("progressEnergy").style.width = energy + "%";
    }

    const incrementEnergy = () => {
        if (energy < 100){
            setEnergy(energy => energy + 1);
            document.getElementById("progressEnergy").style.width = energy + "%";
        }
    }

    useEffect(() => {
        let timer = setInterval(incrementEnergy, 1000);
        return () => {
            clearInterval(timer);
        }
    },[]);

    return (
        <div className="RootApp">
            <Routes>
                <Route path="/" element={<MainPage clickAction={handleClick} money={money} energy={energy}/>} />
                <Route path="/store" element={<StorePage money={money} />} />
                <Route path="/quests" element={<QuestsPage />} />
                <Route path="/referals" element={<ReferalsPage />} />
                <Route path="/profile" element={<ProfilePage />} />
                <Route path="/*" element={<ErrorPage />} />
            </Routes>
            <Navigation />
        </div>
    );
};
