import React, {useState} from "react";
import Counters from "../Counters";
import Tabs from "../Tabs";
import TabContent from "../TabContent";
import CardStore from "../CardStore";
import "../styles/Page.css";
import "../styles/StorePage.css";
import gamepadStoreIcon from "../images/gamepadStore.png";
import energyStoreIcon from "../images/energyStore.png";
import coinLogoIcon from "../images/CoinIcon.png";

export function StorePage(props){

    const [Modal, setModal] = useState({});

    let cardsConsole = [{
        cardName: "Монет за Тап",
        cardImage: gamepadStoreIcon,
        cardUpdateType: "click",
        cardUpdateData: 1,
        cardLevel: 1,
        cardMaxLevel: 3,
        cardDescription: "Эта карточка — настоящий подарок для тех, кто любит зарабатывать. При её прокачке вы получаете дополнительную прибыль за каждый тап. Так что теперь каждое ваше действие будет приносить ещё больше денег!",
        cardPrice: 50
    },
    {
        cardName: "Банка Энергии",
        cardImage: energyStoreIcon,
        cardUpdateType: "energy",
        cardUpdateData: 10,
        cardLevel: 1,
        cardMaxLevel: 3,
        cardDescription: "Эта карточка — настоящее сокровище для любителей игры. При её прокачке вы получаете дополнительную энергию, благодаря которой можете совершать больше тапов. Теперь у вас есть возможность увеличить свою продуктивность и заработать ещё больше очков!",
        cardPrice: 100
    }
    ];

    const openModal = (event) => {
        var modal = document.getElementById("myModal");
        var span = document.getElementsByClassName("close")[0];

        let nameThisCard = event.target.getAttribute("name");
        const card = cardsConsole.find(card => card.cardName === nameThisCard);
        setModal({name: card.cardName, image: card.cardImage, description: card.cardDescription, level: card.cardLevel, price: card.cardPrice});
        modal.style.display = "block";
        span.onclick = function() {
            modal.style.display = "none";
        }
        window.onclick = function(event) {
            if (event.target == modal) {
                modal.style.display = "none";
            }
        }
    }


    return (
        <div className="StorePage Page">
            <Counters isDisabledDataCounters={false} money={props.money} moneyOnTap={props.moneyOnTap} />
            <Tabs />
            <TabContent>
                <div className="StoreCardsConsole StoreCards">
                    {cardsConsole.map((item, index) => <CardStore cardName={item.cardName} image={item.cardImage} cardUpdateData={item.cardUpdateData} cardUpdateType={item.cardUpdateType} cardPrice={item.cardPrice} cardLevel={item.cardLevel} onClick={openModal} key={index} />)}
                </div> 
            </TabContent>
            <div id="myModal" className="modal">
                <div className="modal-content">
                    <div className="modal-content-top">
                        <span className="close">&times;</span>
                        <p>{Modal.name}</p>
                    </div>
                    <div className="modal-content-main">
                        <div className="modal-content-image">
                            <img src={Modal.image} />
                        </div>
                        <p className="modal-content-description">
                            {Modal.description}
                        </p>
                        <div className="modal-content-buy-data-box">
                            <p className="modal-level-data">
                                Уровень карты : {Modal.level}
                            </p>
                            <p className="modal-price-data">
                                Стоимость : {Modal.price}
                                <img src={coinLogoIcon} />
                            </p>
                        </div>
                        <button className="modal-buy-button" onClick={props.buyCardAction}>
                            Купить
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};
