import React, {useState} from "react";
import "./styles/CardStore.css";
import coinImage from "./images/CoinIcon.png";
export default function CardStore(props){

    const [nameModal, setNameModal] = useState('');

    let updateTypeText = ' за тап';
    if (props.cardUpdateType === 'energy'){
        updateTypeText = ' к максимальной энергии'
    }

    const openModal = (event) => {
        setNameModal(name => name + '1');
        var modal = document.getElementById("myModal");
        var span = document.getElementsByClassName("close")[0];
        modal.style.display = "block";
        span.onclick = function() {
            modal.style.display = "none";
        }
        // window.onclick = function(event) {
        //     if (event.target == modal) {
        //         modal.style.display = "none";
        //     }
        // }
    }

    return (
        <div className="CardStore">
            <div className="CardName">
                {props.cardName}
            </div>
            <div className="CardImageBox">
                <img src={props.image} className="CardImage" />
            </div>
            <div className="CardData">
                <div className="CardUpdateData">
                    +{props.cardUpdateData}{updateTypeText}
                </div>
                <div className="CardMainInfo">
                    <button value={props.cardUpdateType} className="CardBuyButton" onClick={openModal} name={props.cardName} >
                        <span className="CardLevel" name={props.cardName} >{props.cardLevel} ур.</span>
                        <div className="CardPriceBox" name={props.cardName}>
                            <span className="CardPrice" name={props.cardName} >{props.cardPrice}</span>
                            <img src={coinImage} name={props.cardName} className="CardPriceCoinImage" />
                        </div>
                    </button>
                </div>
            </div>
            <div id="myModal" class="modal">
                <div class="modal-content">
                    <div className="modal-content-top">
                        <span class="close">&times;</span>
                        <p>{nameModal}</p>
                    </div>
                </div>
            </div>
        </div>
    );
}
